1.3.1 Function(DEPRECATED)
pkg/scheduler/core/generic_scheduler.go:682


  // DEPRECATED: we can remove this when all priorityConfigs implement the
    // Map-Reduce pattern.
    for i := range priorityConfigs {
        if priorityConfigs[i].Function != nil {
